<script>
var idp = null;

		$(function() { 
			$(".idpico").click(
				function(){ 
					idp = $( this ).attr( "idp" );

					switch( idp ){
						case "twitter" : case "facebook": case "odnoklassniki" : case "vkontakte" :						
										start_auth( "http://www.klerk.ru/hybridauth/auth.php?provider=" + idp );
										break; 
						case "livejournal" :  
										if( idp == "blogger" ){
											$("#openidm").html( "Please enter your blog name" );
										}
										else{
											$("#openidm").html( "Please enter your username" );
										}
										$("#openidun").css( "width", "220" );
										$("#openidimg").attr( "src", "images/icons/" + idp + ".png" );
										$("#idps").hide();
										$("#openidid").show();  
										break;
						case "openid" : 
										$("#openidm").html( "Please enter your OpenID URL" );
										$("#openidun").css( "width", "350" );
										$("#openidimg").attr( "src", "images/icons/" + idp + ".png" );
										$("#idps").hide();
										$("#openidid").show();  
										break;

						default: alert( "u no fun" );
					}
				}
			); 

			$("#openidbtn").click(
				function(){
					oi = un = $( "#openidun" ).val();

					if( ! un ){
						alert( "nah not like that! put your username or blog name on this input field." );
						
						return false;
					}

					switch( idp ){ 
						case "wordpress" : oi = "http://" + un + ".wordpress.com"; break;
						case "livejournal" : oi = "http://" + un + ".livejournal.com"; break;
						case "blogger" : oi = "http://" + un + ".blogspot.com"; break;
						case "flickr" : oi = "http://www.flickr.com/photos/" + un + "/"; break;   
					}
					
					start_auth( "?provider=OpenID&openid_identifier=" + escape( oi ) ); 
				}
			);  

			$("#backtolist").click(
				function(){
					$("#idps").show();
					$("#openidid").hide();

					return false;
				}
			);  
		});

		function start_auth( params ){
            var host = window.location.host.split('.').slice(-3).shift();
            if (host != 'forum' && host != 'blogs') {
                host = 'klerk';
            }
			start_url = params + "&distinct=" + host + "&return_to=http%3A%2F%2Fwww.klerk.ru%2Fhybridauth%2Flogin.php" + "&_ts=" + (new Date()).getTime();
			window.open(
				start_url, 
				"hybridauth_social_sing_on", 
				"location=0,status=0,scrollbars=1,width=800,height=500,left=300,top=200"
			);  
		}
</script>

